import { CHANGE_TITLE } from './types';

const INITIAL_STATE = {
    eventTitle: ""
}

const reducer = (state=INITIAL_STATE, action) => {
    switch (action.type) {
        case CHANGE_TITLE: 
            return {
                ...state, 
                eventTitle: action.payload.title
            }
    }

}

export default reducer;
