import { CHANGE_SCHEDULE, CHANGE_TITLE } from "./types";

export const changeTitle = (text) => {
    return {
        type: CHANGE_TITLE,
        payload: text
    };
};

export const changeSchedule = () => {
    return {
        type: CHANGE_SCHEDULE,
    };
};
