import { CHANGE_TITLE } from './types';

const INITIAL_STATE = {
    eventTitle: null
}

const reducer = (state=INITIAL_STATE, action) => {
    switch (action.type) {
        case CHANGE_TITLE: 
            return {
                ...state, 
                eventTitle: action.payload
            }
        default:
            return {
                ...state
            }
    }

}

export default reducer;
