import { CHANGE_ADD_TIME, CHANGE_SCHEDULE, CHANGE_TITLE } from './types';

const INITIAL_STATE = {
    eventTitle: null,
    eventSchedule: null,
    eventAddTime: null
}

const reducer = (state=INITIAL_STATE, action) => {
    switch (action.type) {
        case CHANGE_TITLE: 
            return {
                ...state, 
                eventTitle: action.payload
            }
        case CHANGE_SCHEDULE:
            return {
                ...state, 
                eventSchedule: action.payload
            }
        case CHANGE_ADD_TIME:
            return {
                ...state,
                eventAddTime: action.payload
            }
        default:
            return {
                ...state
            }
    }

}

export default reducer;
